use farmacia;
CREATE TABLE IF NOT EXISTS `venda`(`cod` Integer NOT NULL AUTO_INCREMENT, `subtotal` Double, `desconto` Double,`valorTotal` Double , `data` Date , PRIMARY KEY (`cod`), UNIQUE KEY `cod` (`cod`) )ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `itensVenda`(`cod` Integer NOT NULL AUTO_INCREMENT, `cod_produto` Integer, `qtd_produto` Integer,`cod_venda` Integer, PRIMARY KEY (`cod`), UNIQUE KEY `cod` (`cod`), CONSTRAINT `fk_cod_produto` FOREIGN KEY  ( `cod_produto` ) REFERENCES `Produto`(`cod`) ON UPDATE RESTRICT ON DELETE RESTRICT,CONSTRAINT `fk_cod_venda` FOREIGN KEY  ( `cod_venda` ) REFERENCES `Venda`(`cod`) ON UPDATE RESTRICT ON DELETE RESTRICT )ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `usuario`(`login` varchar(20) NOT NULL, `senha` varchar(50) NOT NULL,`admin` boolean, PRIMARY KEY (`login`), UNIQUE KEY `login` (`login`) )ENGINE=InnoDB DEFAULT CHARSET=utf8;